INSERT INTO `pages1` (`id`, `name`, `alias`, `caption1`, `caption2`, `text1`, `text2`, `images`, `img`, `created_at`, `updated_at`) VALUES
(1, 'Welcome1', 'container1', 'Добро пожаловать', 'В один из самых титулованных и старейших клубов Англии', 'Арсена́л —  профессиональный футбольный клуб из Северного Лондона, выступающий в Премьер-лиге. Основан в 1886 году. Клуб 13 раз становился чемпионом Англии, 13 раз выигрывал Кубок Англии. Домашним стадионом клуба является «Эмирейтс», вмещающий 59 867 зрителей', 'Арсенал провёл бо́льшую часть своей истории в высшем дивизионе английского футбола, в 1992 году стал одним из клубов-основателей английской Премьер-лиги. Также клуб является рекордсменом по количеству сезонов подряд, проведённых в высшем дивизионе чемпионата Англии, начиная с 1919 года.', 'arsenal2019log.jpg', 'img1', NULL, NULL),
(2, 'Welcome1', 'container2', '', '', '', '', 'arsenal2019log1.jpg', 'img2', NULL, NULL),
(3, 'About1', 'about1', ' Арсенал', 'История становления клуба', 'Клуб «Арсенал» был основан в 1886 году на юго-востоке\r\n Лондона рабочими артиллерийского завода «Ройал Арсенал», который \r\nрасполагается в Вулидже. Первоначально клуб назывался «Дайал Скуэр».\r\nСвой первый матч «Арсенал» провел 11 декабря 1886 года против «Истерн Уондерерс». \r\nПобеду одержали «канониры» со счётом 6:0.Спустя две недели на Рождество было организовано \r\nсобрание игроков, на котором было решено переименовать клуб в Ройал Арсенал.', '', '', '', NULL, NULL),
(4, 'About2', 'about2', '', 'Эра Арсена Венгера', 'Уже второй сезон под руководством Венгера «Арсенал» завершил «дублем». \r\nПосле половины чемпионата «канониры» уступали «Манчестер Юнайтед» 12 очков, \r\nи некоторые букмекеры начали выплачивать ставки на чемпионство «манкунианцев». \r\nОднако после этого «Арсенал» выиграл 15 матчей, 3 матча закончились ничьей, и \r\nза тур до завершения чемпионата лондонцы обеспечили себе титул. Арсен Венгер \r\nстал первым иностранным тренером, который выиграл чемпионат Англии.', '', '', '', NULL, NULL);

